func TitleFile(t *testing.T) {
	// 创建新文档
	bookDoc := document.New()
	// 添加引言
	bookDoc.AddHeadingParagraphWithBookmark("第一章 引言", 1, "第一章 引言")
	bookDoc.AddHeadingParagraphWithBookmark("第一章 引言", 1, "第一章 引言")
	config := &document.TOCConfig{
		MaxLevel:     3,
		ShowPageNum:  true,
		RightAlign:   true,
		UseHyperlink: true,
		DotLeader:    true,
	}
	bookDoc.GenerateTOC(config)
	// 添加引言
	bookDoc.AddHeadingParagraphWithBookmark("第二章 引言", 1, "第二章 引言")
	bookDoc.AddHeadingParagraph("第四章 主要功能与特点", 2)
	bookDoc.AddHeadingParagraph("第四章 主要功能与特点", 2)
	bookDoc.AddHeadingParagraph("第四章 主要功能与特点", 3)
	bookDoc.UpdateTOC(config)

	// 保存文档
	if err := bookDoc.Save("example.docx"); err != nil {
		log.Fatal(err)
	}
}

func ImageFile(t *testing.T) {
	base64Text := ""
	decoded, _ := base64.StdEncoding.DecodeString(base64Text)
	width, height, _ := utils.ImagePngWithDecode(decoded)
	// 创建新文档
	bookDoc := document.New()
	// 假设 imageData 是图片的字节数据
	bookDoc.AddImageFromData(
		decoded,
		"image.png",             // 文件名
		document.ImageFormatPNG, // 图片格式
		width, height,           // 原始宽度和高度（像素）
		&document.ImageConfig{
			Size: &document.ImageSize{
				Width:           130.0 * math.Min(float64(width)/float64(height), 1), // 显示宽度（毫米）
				KeepAspectRatio: true,
			},
			Position:  document.ImagePositionInline,
			Alignment: document.AlignCenter,
		},
	)

	// 保存文档
	if err := bookDoc.Save("example.docx"); err != nil {
		log.Fatal(err)
	}
}

