func CodeTemplate(t *testing.T) {
	// 1. 创建模板引擎
	engine := document.NewTemplateEngine()

	// 2. 打开现有的DOCX模板文件
	templateDoc, err := document.Open("example.docx")
	if err != nil {
		log.Fatalf("无法打开模板文件: %v", err)
	}

	// 3. 从文档创建模板
	_, err = engine.LoadTemplateFromDocument("contract_template", templateDoc)
	if err != nil {
		log.Fatalf("从文档创建模板失败: %v", err)
	}

	// 4. 准备数据
	data := document.NewTemplateData()
	data.SetVariable("SOFTWARE_NAME_AND_VERSION", "科技V1.5.0")
	data.SetVariable("CODE_CONTENT", "WordZero\n科技")

	// 5. 渲染生成合同
	contractDoc, err := engine.RenderTemplateToDocument("contract_template", data)
	if err != nil {
		log.Fatalf("渲染合同失败: %v", err)
	}

	// 6. 保存生成的合同
	err = contractDoc.Save("test.docx")
	if err != nil {
		log.Fatalf("保存合同失败: %v", err)
	}
}
